import React, { useState } from "react";
import Navbar from "../Components/Layouts/Navbar";

const Profile = () => {
  const [avatarURL, setAvatarURL] = useState(
    localStorage.getItem("avatarURL") || ""
  );
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const profileName = localStorage.getItem("userName");

  const handleAvatarURLChange = (event) => {
    setAvatarURL(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const authToken = localStorage.getItem("accessToken");
      const profileName = localStorage.getItem("userName");

      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${profileName}/media`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            avatar: avatarURL,
            banner: null,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update profile media");
      }

      setSuccess(true);
    } catch (error) {
      console.error("Error updating profile media:", error);
      setError("Failed to update profile media");
    }
  };

  return (
    <div>
      <Navbar />
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="avatarURL">Avatar URL:</label>
          <input
            type="text"
            id="avatarURL"
            value={avatarURL}
            onChange={handleAvatarURLChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      {error && <p>Error: {error}</p>}
      {success && (
        <div>
          <p>Avatar uploaded successfully!</p>
          <img src={avatarURL} alt="Avatar" style={{ width: "200px" }} />
        </div>
      )}
    </div>
  );
};

export default Profile;
