import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";

const PostSpecific = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const [commentInput, setCommentInput] = useState("");

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/posts/${id}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          if (response.status === 404) {
            setError("Post not found");
          } else {
            throw new Error("Failed to fetch post");
          }
        } else {
          const postData = await response.json();
          setPost(postData);
        }
      } catch (error) {
        console.error("Error fetching post:", error);
        setError(error.message);
      }
    };

    fetchPost();
  }, [id]);

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${id}/comment`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
          body: JSON.stringify({ body: commentInput }),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to submit comment");
      }
      console.log("Comment submitted successfully!");
      const updatedPostData = await response.json();
      setPost((prevPost) => ({
        ...prevPost,
        comments: [...(prevPost.comments || []), updatedPostData.comments],
      }));
    } catch (error) {
      console.error("Error submitting comment:", error);
    }
  };

  const handleRemoveComment = async (index) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${id}/comment/${index}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to remove comment");
      }
      console.log("Comment removed successfully!");
      const postData = await response.json();
      setPost(postData);
    } catch (error) {
      console.error("Error removing comment:", error);
    }
  };

  const handleReactToPost = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${id}/react`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to react to post");
      }
      console.log("Reacted to post successfully!");
      const postData = await response.json();
      setPost(postData);
    } catch (error) {
      console.error("Error reacting to post:", error);
    }
  };

  const handleCommentInputChange = (e) => {
    setCommentInput(e.target.value);
  };

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!post) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Navbar />
      <div className="post-specific-content">
        <h1>{post.title}</h1>
        <p>{post.body}</p>
        <button
          onClick={handleReactToPost}
          className="btn btn-link float-end"
          style={{ fontSize: "1.2rem" }}
        >
          {post.reacted ? (
            <i className="bi bi-heart-fill text-danger"></i>
          ) : (
            <i className="bi bi-heart text-danger"></i>
          )}
        </button>
        <form onSubmit={handleCommentSubmit}>
          <div className="form-group">
            <textarea
              className="form-control"
              rows="3"
              value={commentInput}
              onChange={handleCommentInputChange}
              placeholder="Write a comment..."
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
        {post.comments && post.comments.length > 0 && (
          <ul className="list-group list-group-flush">
            {post.comments.map((comment, index) => (
              <li key={index} className="list-group-item">
                {comment.body}
                <button
                  className="btn btn-link float-end"
                  onClick={() => handleRemoveComment(post.id, index)}
                >
                  <i className="bi bi-x text-danger"></i>
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PostSpecific;
