import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";

const Profiles = () => {
  const { name } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [actionInProgress, setActionInProgress] = useState(false);

  const fetchProfile = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch profile");
      }
      const responseData = await response.json();
      setProfile(responseData);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching profile:", error.message);
      setError("Failed to fetch profile");
      setLoading(false);
    }
  };

  const toggleFollow = async () => {
    try {
      setActionInProgress(true);
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/${
          profile.is_followed_by_current_user ? "unfollow" : "follow"
        }`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error(
          `Failed to ${
            profile.is_followed_by_current_user ? "unfollow" : "follow"
          } profile`
        );
      }
      const updatedProfile = {
        ...profile,
        is_followed_by_current_user: !profile.is_followed_by_current_user,
        _count: {
          ...profile._count,
          followers:
            profile._count.followers +
            (profile.is_followed_by_current_user ? -1 : 1),
        },
      };
      setProfile(updatedProfile);
      updateFollowStatusInLocalStorage(
        name,
        updatedProfile.is_followed_by_current_user
      );
      setActionInProgress(false);
    } catch (error) {
      console.error(
        `Error ${
          profile.is_followed_by_current_user ? "unfollowing" : "following"
        } profile:`,
        error
      );
      setActionInProgress(false);
    }
  };

  const updateFollowStatusInLocalStorage = (name, isFollowing) => {
    const followedProfiles =
      JSON.parse(localStorage.getItem("followedProfiles")) || {};
    followedProfiles[name] = isFollowing;
    localStorage.setItem("followedProfiles", JSON.stringify(followedProfiles));
  };

  useEffect(() => {
    fetchProfile();
  }, [name]);

  useEffect(() => {
    const followedProfiles =
      JSON.parse(localStorage.getItem("followedProfiles")) || {};
    const isFollowing = followedProfiles[name] || false;
    if (profile) {
      setProfile((prevProfile) => ({
        ...prevProfile,
        is_followed_by_current_user: isFollowing,
      }));
    }
  }, [profile, name]);

  return (
    <div>
      <Navbar />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : !profile ? (
        <p>Profile not found</p>
      ) : (
        <div>
          <h1>Profile Page for {name}</h1>
          <p>
            Avatar:{" "}
            <img src={profile.avatar} alt="Avatar" style={{ width: "200px" }} />
          </p>
          <p>
            Banner:{" "}
            <img src={profile.banner} alt="Banner" style={{ width: "200px" }} />
          </p>
          <p>Name: {profile.name}</p>
          <p>Followers: {profile._count.followers}</p>
          <p>Following: {profile._count.following}</p>
          <p>Posts: {profile._count.posts}</p>
          {actionInProgress ? (
            <p>Updating follow status...</p>
          ) : (
            <button onClick={toggleFollow}>
              {profile.is_followed_by_current_user ? "Unfollow" : "Follow"}
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default Profiles;
