import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";
import { Link } from "react-router-dom";
import CommentForm from "./Post/CommentForm";
import ReactToPost from "./Post/ReactToPost";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const fetchPosts = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        "https://nf-api.onrender.com/api/v1/social/posts",
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch posts");
      }
      const postData = await response.json();
      setPosts(postData);
    } catch (error) {
      console.error("Error fetching posts:", error);
      setError(error.message);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const handleCommentSubmit = async (postId, comment) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}/comment`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
          body: JSON.stringify({ body: comment }),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to submit comment");
      }
      console.log("Comment submitted successfully!");

      setPosts((prevPosts) =>
        prevPosts.map((post) =>
          post.id === postId
            ? {
                ...post,
                comments: [...(post.comments || []), { body: comment }],
              }
            : post
        )
      );
    } catch (error) {
      console.error("Error submitting comment:", error);
    }
  };

  const handleReactToPost = async (postId, symbol) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}/react/${symbol}`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to react to post");
      }
      console.log("Reacted to post successfully!");
    } catch (error) {
      console.error("Error reacting to post:", error);
    }
  };

  return (
    <div>
      <Navbar />
      <h2>List of Posts</h2>
      {error && <p>Error: {error}</p>}
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <Link to={`/posts/${post.id}`} key={post.id}>
              {post.title}
            </Link>
            <CommentForm
              postId={post.id}
              onCommentSubmit={handleCommentSubmit}
            />
            <ReactToPost
              postId={post.id}
              symbol="❤️"
              onReactToPost={handleReactToPost}
            />
            <ReactToPost
              postId={postId}
              symbols={["❤️", "👍", "😄"]}
              onReactToPost={handleReactToPost}
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Posts;
