import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";
import "./Posts.css";

const PostSpecific = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const [commentInputs, setCommentInputs] = useState("");
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/posts/${id}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch post");
        }
        const postData = await response.json();
        setPost(postData);
      } catch (error) {
        console.error("Error fetching post:", error);
        setError(error.message);
      }
    };

    fetchPost();

    const storedComments = JSON.parse(
      localStorage.getItem(`post_${id}_comments`)
    );
    if (storedComments) {
      setComments(storedComments);
    }
  }, [id]);

  const handleCommentSubmit = async (comment) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${id}/comment`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
          body: JSON.stringify({ body: comment }),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to submit comment");
      }
      console.log("Comment submitted successfully!");

      const newComments = [...comments, { body: comment }];
      setComments(newComments);

      localStorage.setItem(`post_${id}_comments`, JSON.stringify(newComments));

      clearCommentInput();
    } catch (error) {
      console.error("Error submitting comment:", error);
    }
  };

  const handleCommentInputChange = (value) => {
    setCommentInputs(value);
  };

  const clearCommentInput = () => {
    setCommentInputs("");
  };

  const handleRemoveComment = (index) => {
    const updatedComments = [...comments];
    updatedComments.splice(index, 1);
    setComments(updatedComments);
    localStorage.setItem(
      `post_${id}_comments`,
      JSON.stringify(updatedComments)
    );
  };

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!post) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Navbar />
      <div className="post-specific-content">
        <div className="card">
          <div className="card-body">
            <h1>{post.title}</h1>
            <p className="centered">{post.body}</p>
            <p>
              Posted by:{" "}
              {post.user && post.user.name ? post.user.name : "Unknown"}
            </p>

            <form
              onSubmit={(e) => {
                e.preventDefault();
                handleCommentSubmit(commentInputs);
              }}
            >
              <textarea
                value={commentInputs}
                onChange={(e) => handleCommentInputChange(e.target.value)}
              />
              <button type="submit">Submit</button>
            </form>
            <div>
              <ul>
                {comments.map((comment, index) => (
                  <li key={index}>
                    {comment.body}
                    <button onClick={() => handleRemoveComment(index)}>
                      Delete
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostSpecific;
