import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";
import "./Home.css";

const Home = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const position = window.scrollY;
      setScrollPosition(position);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const getBackgroundColor = () => {
    const opacity = Math.min(scrollPosition / 100, 1);
    return `rgba(103, 136, 130, ${opacity})`;
  };

  return (
    <div className="home" style={{ backgroundColor: getBackgroundColor() }}>
      <Navbar />
      <h1>LinkUp</h1>
      <p>
        Connect with friends and family seamlessly with our secure messaging
        platform. Experience fast, reliable, and encrypted communication.
      </p>
      <button type="button" className="btn btn-primary">
        Register
      </button>
      <button type="button" className="btn btn-primary">
        Login
      </button>
    </div>
  );
};

export default Home;
