import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";

const Profiles = () => {
  const { name } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch profile");
        }
        const responseData = await response.json();
        setProfile(responseData);

        const followStatus = localStorage.getItem("followStatus");
        if (followStatus) {
          const followStatusObj = JSON.parse(followStatus);
          setIsFollowing(followStatusObj[name] || false);
        }
        setLoading(false);
      } catch (error) {
        console.error("Error fetching profile:", error.message);
        setError("Failed to fetch profile");
        setLoading(false);
      }
    };

    fetchProfile();
  }, [name]);

  const handleFollow = async () => {
    try {
      setIsFollowing(true);

      const followStatus =
        JSON.parse(localStorage.getItem("followStatus")) || {};
      localStorage.setItem(
        "followStatus",
        JSON.stringify({ ...followStatus, [name]: true })
      );
    } catch (error) {
      console.error("Error following profile:", error);
    }
  };

  const handleUnfollow = async () => {
    try {
      setIsFollowing(false);

      const followStatus =
        JSON.parse(localStorage.getItem("followStatus")) || {};
      localStorage.setItem(
        "followStatus",
        JSON.stringify({ ...followStatus, [name]: false })
      );
    } catch (error) {
      console.error("Error unfollowing profile:", error);
    }
  };

  return (
    <div>
      <Navbar />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : !profile ? (
        <p>Profile not found</p>
      ) : (
        <div>
          <h1>Profile Page for {name}</h1>
          <button onClick={isFollowing ? handleUnfollow : handleFollow}>
            {isFollowing ? "Unfollow" : "Follow"}
          </button>
        </div>
      )}
    </div>
  );
};

export default Profiles;
