import React, { useState } from "react";
import Navbar from "../Components/Layouts/Navbar";

const Profile = () => {
  const [avatar, setAvatar] = useState(null);
  const [error, setError] = useState(null);

  const handleAvatarChange = (event) => {
    setAvatar(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    if (avatar) formData.append("avatar", avatar);

    try {
      const name = localStorage.getItem("name");

      if (!name) {
        throw new Error("Name not found in local storage");
      }

      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/media`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            avatar: avatar ? URL.createObjectURL(avatar) : null,
            banner: null,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update profile media");
      }
    } catch (error) {
      console.error("Error updating profile media:", error);
      setError("Failed to update profile media");
    }
  };

  return (
    <div>
      <Navbar />
      <form onSubmit={handleSubmit}>
        <div>
          <label>Avatar:</label>
          <input type="file" accept="image/*" onChange={handleAvatarChange} />
        </div>
        <button type="submit">Submit</button>
      </form>
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default Profile;
