import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";
import Post from "./Posts";

const Profiles = () => {
  const { name } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userPosts, setUserPosts] = useState([]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch profile");
        }
        const responseData = await response.json();
        setProfile(responseData);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching profile:", error.message);
        setError("Failed to fetch profile");
        setLoading(false);
      }
    };

    fetchProfile();
  }, [name]);

  useEffect(() => {
    const fetchUserPosts = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}/posts`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch user posts");
        }
        const userPostsData = await response.json();
        setUserPosts(userPostsData);
      } catch (error) {
        console.error("Error fetching user posts:", error.message);
      }
    };

    if (profile) {
      fetchUserPosts();
    }
  }, [name, profile]);

  return (
    <div>
      <Navbar />
      <div className="container">
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>Error: {error}</p>
        ) : !profile ? (
          <p>Profile not found</p>
        ) : (
          <>
            <div className="row justify-content-center">
              <div className="col-md-8">
                <h1 className="text-center">Profile Page for {name}</h1>
                <div className="card" id="profile-card">
                  <div className="card-body" id="profile-body">
                    <div className="text-center">
                      <p>
                        <img
                          src={profile.avatar}
                          alt="Avatar"
                          style={{ maxWidth: "200px" }}
                        />
                      </p>
                      <p>
                        <img
                          src={profile.banner}
                          alt="Banner"
                          style={{ maxWidth: "200px" }}
                        />
                      </p>
                      <p>Name: {profile.name}</p>
                      <p>Followers: {profile._count.followers}</p>
                      <p>Following: {profile._count.following}</p>
                      <p>Posts: {profile._count.posts}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="row justify-content-center">
              <div className="col-md-8">
                {userPosts.length > 0 ? (
                  <>
                    <h2 className="text-center">User Posts</h2>
                    {userPosts.map((post) => (
                      <Post key={post.id} title={post.title} body={post.body} />
                    ))}
                  </>
                ) : (
                  <p className="text-center">No posts yet</p>
                )}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Profiles;
