import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";
import CreatePostForm from "./Post/CreatePostForm";
import DeletePost from "./Post/DeletePost";
import EditPost from "./Post/EditPost";
import "./MyProfile.css";

const MyProfile = () => {
  const [avatarURL, setAvatarURL] = useState("");
  const [bannerURL, setBannerURL] = useState("");
  const [error, setError] = useState(null);
  const [showCreatePostForm, setShowCreatePostForm] = useState(false);
  const [posts, setPosts] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const [successMessageVisible, setSuccessMessageVisible] = useState(false);

  const profileName = localStorage.getItem("userName");

  useEffect(() => {
    const storedAvatarURL = localStorage.getItem("avatarURL");
    if (storedAvatarURL) {
      setAvatarURL(storedAvatarURL);
    }

    const storedBannerURL = localStorage.getItem("bannerURL");
    if (storedBannerURL) {
      setBannerURL(storedBannerURL);
    }

    const fetchUserPosts = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${profileName}/posts`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch user's posts");
        }
        const postData = await response.json();
        setPosts(postData);
      } catch (error) {
        console.error("Error fetching user's posts:", error);
        setError("Failed to fetch user's posts");
      }
    };

    fetchUserPosts();
  }, []);

  const handleAvatarURLChange = (event) => {
    setAvatarURL(event.target.value);
  };

  const handleBannerURLChange = (event) => {
    setBannerURL(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const authToken = localStorage.getItem("accessToken");

      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${profileName}/media`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            avatar: avatarURL,
            banner: bannerURL,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update profile media");
      }

      localStorage.setItem("avatarURL", avatarURL);
      localStorage.setItem("bannerURL", bannerURL);

      console.log("Avatar and Banner URLs updated successfully!");
    } catch (error) {
      console.error("Error updating profile media:", error);
      setError("Failed to update profile media");
    }
  };

  const handleCreatePostClick = () => {
    setShowCreatePostForm(true);
  };

  const handlePostDeleted = async (postId) => {
    try {
      const authToken = localStorage.getItem("accessToken");

      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );

      if (response.ok || response.status === 204) {
        setPosts(posts.filter((post) => post.id !== postId));
        setSuccessMessage("Post deleted successfully!");
        setSuccessMessageVisible(true);
      } else {
        throw new Error("Failed to delete post");
      }
    } catch (error) {
      console.error("Error deleting post:", error);
      setError("Failed to delete post");
    }
  };

  return (
    <div>
      <Navbar />
      {bannerURL && (
        <div className="banner-container">
          <img src={bannerURL} alt="Banner" className="banner-image" />
        </div>
      )}
      {avatarURL && (
        <div className="card" style={{ width: "18rem" }}>
          <img src={avatarURL} className="card-img-top" alt="Avatar" />
          <div className="card-body">
            <p className="card-text">Update your avatar or banner image.</p>
          </div>
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="avatarURL">Avatar URL:</label>
          <input
            type="text"
            id="avatarURL"
            value={avatarURL}
            onChange={handleAvatarURLChange}
          />
        </div>
        <div>
          <label htmlFor="bannerURL">Banner URL:</label>
          <input
            type="text"
            id="bannerURL"
            value={bannerURL}
            onChange={handleBannerURLChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      <button onClick={handleCreatePostClick}>Create Post</button>
      {showCreatePostForm && <CreatePostForm />}
      <Link to="/posts">View Posts</Link>
      {error && <p>Error: {error}</p>}
      <h2>Your Posts:</h2>
      {posts.map((post) => (
        <div key={post.id}>
          <h3>{post.title}</h3>
          <p>{post.body}</p>
          <EditPost postId={post.id} />
          <DeletePost postId={post.id} onPostDeleted={handlePostDeleted} />
        </div>
      ))}
      {successMessageVisible && <p>{successMessage}</p>}
    </div>
  );
};

export default MyProfile;
