import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";
import { useParams } from "react-router-dom";

const Profiles = () => {
  const { name } = useParams();
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch profile");
        }
        const profile = await response.json();
        setProfileData(profile);
      } catch (error) {
        console.error("Error fetching profile:", error.message);
      }
    };

    fetchProfileData();
  }, [name]);

  if (!profileData) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Navbar />
      <h1>Profile Page for {name}</h1>
      <p>
        Avatar:{" "}
        <img src={profileData.avatar} alt="Avatar" style={{ width: "200px" }} />
      </p>
      <p>
        Banner:{" "}
        <img src={profileData.banner} alt="Banner" style={{ width: "200px" }} />
      </p>
      <p>Name: {profileData.name}</p>
      <p>Followers: {profileData._count.followers}</p>
      <p>Following: {profileData._count.following}</p>
      <p>Posts: {profileData._count.posts}</p>
    </div>
  );
};

export default Profiles;
