import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";
import "./Profiles.css";

const Profiles = () => {
  const { name } = useParams();
  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState({});
  const [actionInProgress, setActionInProgress] = useState({});

  useEffect(() => {
    const fetchProfiles = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch profiles");
        }
        const profilesData = await response.json();
        setProfiles(profilesData);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching profiles:", error);
        setError("Failed to fetch profiles");
        setLoading(false);
      }
    };

    fetchProfiles();
  }, []);

  useEffect(() => {
    const fetchFollowStatus = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const statusPromises = profiles.map((profile) =>
          fetch(
            `https://nf-api.onrender.com/api/v1/social/profiles/${profile.name}/follow`,
            {
              headers: {
                Authorization: `Bearer ${authToken}`,
              },
            }
          )
        );
        const statusResponses = await Promise.all(statusPromises);
        const statusData = await Promise.all(
          statusResponses.map((response) => response.json())
        );
        const followStatus = {};
        statusData.forEach((status, index) => {
          followStatus[profiles[index].name] =
            status.is_followed_by_current_user;
        });
        setIsFollowing(followStatus);
      } catch (error) {
        console.error("Error fetching follow status:", error);
      }
    };

    fetchFollowStatus();
  }, [profiles]);

  const handleFollow = async (profileName) => {
    try {
      if (isFollowing[profileName]) {
        console.log("User is already following this profile");
        return;
      }

      setActionInProgress((prevStatus) => ({
        ...prevStatus,
        [profileName]: true,
      }));
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${profileName}/follow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to follow profile");
      }
      setIsFollowing((prevStatus) => ({
        ...prevStatus,
        [profileName]: true,
      }));
      setActionInProgress((prevStatus) => ({
        ...prevStatus,
        [profileName]: false,
      }));
    } catch (error) {
      console.error("Error following profile:", error);
      setActionInProgress((prevStatus) => ({
        ...prevStatus,
        [profileName]: false,
      }));
    }
  };

  const handleUnfollow = async (profileName) => {
    try {
      setActionInProgress((prevStatus) => ({
        ...prevStatus,
        [profileName]: true,
      }));
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${profileName}/unfollow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to unfollow profile");
      }
      setIsFollowing((prevStatus) => ({
        ...prevStatus,
        [profileName]: false,
      }));
      setActionInProgress((prevStatus) => ({
        ...prevStatus,
        [profileName]: false,
      }));
    } catch (error) {
      console.error("Error unfollowing profile:", error);
      setActionInProgress((prevStatus) => ({
        ...prevStatus,
        [profileName]: false,
      }));
    }
  };

  return (
    <div className="container">
      <Navbar />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div className="row justify-content-center">
          {profiles.map((profile) => (
            <div key={profile.id} className="col-md-4 mb-4">
              <div className="card profile-card">
                <img
                  src={profile.avatar}
                  className="card-img-top"
                  alt="Avatar"
                />
                <div className="card-body">
                  <h5 className="card-title">{profile.name}</h5>
                  <p className="card-text">
                    Followers: {profile._count.followers}
                  </p>
                  {actionInProgress[profile.name] ? (
                    <p>Updating follow status...</p>
                  ) : (
                    <button
                      onClick={() =>
                        isFollowing[profile.name]
                          ? handleUnfollow(profile.name)
                          : handleFollow(profile.name)
                      }
                      className={`btn btn-${
                        isFollowing[profile.name] ? "danger" : "primary"
                      }`}
                    >
                      {isFollowing[profile.name] ? "Unfollow" : "Follow"}
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Profiles;
