import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";

const Profile = () => {
  const [avatarURL, setAvatarURL] = useState("");
  const [bannerURL, setBannerURL] = useState("");
  const [error, setError] = useState(null);

  const profileName = localStorage.getItem("userName");

  useEffect(() => {
    const storedAvatarURL = localStorage.getItem("avatarURL");
    if (storedAvatarURL) {
      setAvatarURL(storedAvatarURL);
    }

    const storedBannerURL = localStorage.getItem("bannerURL");
    if (storedBannerURL) {
      setBannerURL(storedBannerURL);
    }
  }, []);

  const handleAvatarURLChange = (event) => {
    setAvatarURL(event.target.value);
  };

  const handleBannerURLChange = (event) => {
    setBannerURL(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const authToken = localStorage.getItem("accessToken");

      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${profileName}/media`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            avatar: avatarURL,
            banner: bannerURL,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update profile media");
      }

      localStorage.setItem("avatarURL", avatarURL);
      localStorage.setItem("bannerURL", bannerURL);

      console.log("Avatar and Banner URLs updated successfully!");
    } catch (error) {
      console.error("Error updating profile media:", error);
      setError("Failed to update profile media");
    }
  };

  return (
    <div>
      <Navbar />
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="avatarURL">Avatar URL:</label>
          <input
            type="text"
            id="avatarURL"
            value={avatarURL}
            onChange={handleAvatarURLChange}
          />
        </div>
        <div>
          <label htmlFor="bannerURL">Banner URL:</label>
          <input
            type="text"
            id="bannerURL"
            value={bannerURL}
            onChange={handleBannerURLChange}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      {avatarURL && (
        <div>
          <h2>Avatar Preview</h2>
          <img src={avatarURL} alt="Avatar" />
        </div>
      )}
      {bannerURL && (
        <div>
          <h2>Banner Preview</h2>
          <img src={bannerURL} alt="Banner" />
        </div>
      )}
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default Profile;
