import React, { useState } from "react";
import Navbar from "../Components/Layouts/Navbar";

const Profile = () => {
  const [avatar, setAvatar] = useState(null);
  const [banner, setBanner] = useState(null);
  const [error, setError] = useState(null);

  const handleAvatarChange = (event) => {
    setAvatar(event.target.files[0]);
  };

  const handleBannerChange = (event) => {
    setBanner(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const authToken = localStorage.getItem("accessToken");

      const payload = {};
      if (avatar) payload.avatar = URL.createObjectURL(avatar);
      if (banner) payload.banner = URL.createObjectURL(banner);

      const response = await fetch(
        "https://nf-api.onrender.com/api/v1/social/profiles/{name}/media",
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(payload),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update profile media");
      }
    } catch (error) {
      console.error("Error updating profile media:", error);
      setError("Failed to update profile media");
    }
  };

  return (
    <div>
      <Navbar />
      <form onSubmit={handleSubmit}>
        <div>
          <label>Avatar:</label>
          <input type="file" accept="image/*" onChange={handleAvatarChange} />
        </div>
        <div>
          <label>Banner:</label>
          <input type="file" accept="image/*" onChange={handleBannerChange} />
        </div>
        <button type="submit">Submit</button>
      </form>
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default Profile;
