import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";
import { Link } from "react-router-dom";

const Users = () => {
  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchProfiles = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        "https://nf-api.onrender.com/api/v1/social/profiles",
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch profiles");
      }
      const responseData = await response.json();
      setProfiles(responseData);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching profiles:", error.message);
      setError("Failed to fetch profiles");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfiles();
  }, []);

  return (
    <div>
      <div
        className="banner"
        style={{
          backgroundImage: `url('your-banner-url')`,
          backgroundSize: "cover",
          height: "200px",
        }}
      ></div>
      <Navbar />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : profiles.length === 0 ? (
        <p>No profiles found</p>
      ) : (
        <div className="container">
          <div className="row row-cols-1 row-cols-md-3 g-4">
            {profiles.map((profile, index) => (
              <div className="col" key={index}>
                <div className="card h-100" style={{ maxWidth: "300px" }}>
                  <div className="card-body d-flex flex-column justify-content-between">
                    <div>
                      <h5 className="card-title">{profile.name}</h5>
                      <img
                        src={profile.avatar}
                        className="avatar img-fluid"
                        alt={`Avatar for ${profile.name}`}
                        style={{
                          maxWidth: "200px",
                          maxHeight: "200px",
                          marginBottom: "10px",
                        }}
                      />
                    </div>
                    <Link
                      to={`/profile/${profile.name}`}
                      className="btn btn-primary mt-auto"
                    >
                      View Profile
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Users;
