import React, { useState } from "react";
import Navbar from "../Components/Layouts/Navbar";

const NewEstablishment = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    image: "",
    price: "",
    maxGuests: "",
    createdDate: new Date().toLocaleString(),
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      if (files.length > 0) {
        const file = files[0];
        const reader = new FileReader();
        reader.onload = () => {
          setFormData({
            ...formData,
            image: reader.result,
          });
        };
        reader.readAsDataURL(file);
      }
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSend = new FormData();
      formDataToSend.append("name", formData.name);
      formDataToSend.append("description", formData.description);
      formDataToSend.append("image", formData.image);
      formDataToSend.append("price", formData.price);
      formDataToSend.append("maxGuests", formData.maxGuests);

      const authToken = localStorage.getItem("authToken");

      const response = await fetch(
        "https://nf-api.onrender.com/api/v1/holidaze/venues",
        {
          method: "POST",
          body: formDataToSend,
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setFormData({
        name: "",
        description: "",
        image: null,
        price: "",
        maxGuests: "",
        createdDate: new Date().toLocaleString(),
      });
      alert("New venue created successfully!");
    } catch (error) {
      console.error("Error creating venue:", error);
      alert("Failed to create venue. Please try again later.");
    }
  };

  return (
    <div>
      <Navbar />
      <h2>Create a New Venue</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Image:</label>
          <input type="file" name="image" onChange={handleChange} />
        </div>
        <div>
          <label>Price:</label>
          <input
            type="text"
            name="price"
            value={formData.price}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Max Guests:</label>
          <input
            type="number"
            name="maxGuests"
            value={formData.maxGuests}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Create Venue</button>
      </form>
    </div>
  );
};

export default NewEstablishment;
