import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";
import { useParams } from "react-router-dom";

const Profiles = () => {
  const { name } = useParams();
  const [profileData, setProfileData] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const response = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error("Failed to fetch profile");
        }
        const profile = await response.json();
        setProfileData(profile);
      } catch (error) {
        console.error("Error fetching profile:", error.message);
      }
    };

    fetchProfileData();
  }, [name]);

  const handleFollow = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/follow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to follow profile");
      }
      setIsFollowing(true);
    } catch (error) {
      console.error("Error following profile:", error.message);
    }
  };

  const handleUnfollow = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/unfollow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to unfollow profile");
      }
      setIsFollowing(false);
    } catch (error) {
      console.error("Error unfollowing profile:", error.message);
    }
  };

  if (!profileData) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Navbar />
      <h1>Profile Page for {name}</h1>
      <p>
        Avatar: <img src={profileData.avatar} alt="Avatar" />
      </p>
      <p>
        Banner: <img src={profileData.banner} alt="Banner" />
      </p>
      <p>Name: {profileData.name}</p>
      <p>Followers: {profileData._count.followers}</p>
      <p>Following: {profileData._count.following}</p>
      <p>Posts: {profileData._count.posts}</p>
      {isFollowing ? (
        <button onClick={handleUnfollow}>Unfollow</button>
      ) : (
        <button onClick={handleFollow}>Follow</button>
      )}
    </div>
  );
};

export default Profiles;
