import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../Components/Layouts/Navbar";

const Profiles = () => {
  const { name } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);
  const [actionInProgress, setActionInProgress] = useState(false);

  // Function to fetch profile from the API
  const fetchProfile = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch profile");
      }
      const responseData = await response.json();
      setProfile(responseData);
      setIsFollowing(responseData.is_followed_by_current_user);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching profile:", error.message);
      setError("Failed to fetch profile");
      setLoading(false);
    }
  };

  // Function to handle following a profile
  const handleFollow = async () => {
    try {
      if (isFollowing) {
        console.log("User is already following this profile");
        return;
      }

      setActionInProgress(true);
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/follow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to follow profile");
      }
      setIsFollowing(true);
      updateLocalStorage(true); // Update follow status in localStorage
      setProfile((prevProfile) => ({
        ...prevProfile,
        _count: {
          ...prevProfile._count,
          followers: prevProfile._count.followers + 1,
        },
      }));
      setActionInProgress(false);
    } catch (error) {
      console.error("Error following profile:", error);
      setActionInProgress(false);
    }
  };

  // Function to handle unfollowing a profile
  const handleUnfollow = async () => {
    try {
      setActionInProgress(true);
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/unfollow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to unfollow profile");
      }
      setIsFollowing(false);
      updateLocalStorage(false); // Update follow status in localStorage
      setProfile((prevProfile) => ({
        ...prevProfile,
        _count: {
          ...prevProfile._count,
          followers: prevProfile._count.followers - 1,
        },
      }));
      setActionInProgress(false);
    } catch (error) {
      console.error("Error unfollowing profile:", error);
      setActionInProgress(false);
    }
  };

  // Function to update follow status in localStorage
  const updateLocalStorage = (isFollowing) => {
    const followedProfiles =
      JSON.parse(localStorage.getItem("followedProfiles")) || {};
    followedProfiles[name] = isFollowing;
    localStorage.setItem("followedProfiles", JSON.stringify(followedProfiles));
  };

  // Function to check if the profile is followed in localStorage
  const checkFollowStatus = () => {
    const followedProfiles =
      JSON.parse(localStorage.getItem("followedProfiles")) || {};
    setIsFollowing(followedProfiles[name] || false);
  };

  useEffect(() => {
    fetchProfile();
    checkFollowStatus();
  }, [name]);

  return (
    <div>
      <Navbar />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : !profile ? (
        <p>Profile not found</p>
      ) : (
        <div>
          <h1>Profile Page for {name}</h1>
          <p>
            Avatar:{" "}
            <img src={profile.avatar} alt="Avatar" style={{ width: "200px" }} />
          </p>
          <p>
            Banner:{" "}
            <img src={profile.banner} alt="Banner" style={{ width: "200px" }} />
          </p>
          <p>Name: {profile.name}</p>
          <p>Followers: {profile._count.followers}</p>
          <p>Following: {profile._count.following}</p>
          <p>Posts: {profile._count.posts}</p>
          {actionInProgress ? (
            <p>Updating follow status...</p>
          ) : (
            <button onClick={isFollowing ? handleUnfollow : handleFollow}>
              {isFollowing ? "Unfollow" : "Follow"}
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default Profiles;
