import Navbar from "../Components/Layouts/Navbar";
import "./Profiles.css";

const Profiles = () => {
  const { name } = useParams();
  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);
  const [actionInProgress, setActionInProgress] = useState(false);
  const [commentText, setCommentText] = useState("");
  const [comments, setComments] = useState({});

  useEffect(() => {
    const storedFollowStatus = localStorage.getItem(`${name}-follow`);
    setIsFollowing(storedFollowStatus === "true");
  }, [name]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const authToken = localStorage.getItem("accessToken");
        const profileResponse = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!profileResponse.ok) {
          throw new Error("Failed to fetch profile");
        }
        const profileData = await profileResponse.json();
        setProfile(profileData);

        const postsResponse = await fetch(
          `https://nf-api.onrender.com/api/v1/social/profiles/${name}/posts`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        if (!postsResponse.ok) {
          throw new Error("Failed to fetch user's posts");
        }
        const postsData = await postsResponse.json();
        setPosts(postsData);

        setLoading(false);
      } catch (error) {
        console.error("Error fetching profile:", error.message);
        setError("Failed to fetch profile");
        setLoading(false);
      }
    };

    fetchProfile();
  }, [name]);

  const handleFollow = async () => {
    try {
      setActionInProgress(true);
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/follow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to follow profile");
      }
      setIsFollowing(true);
      setActionInProgress(false);
      localStorage.setItem(`${name}-follow`, "true");
      setProfile((prevProfile) => ({
        ...prevProfile,
        _count: {
          ...prevProfile._count,
          followers: prevProfile._count.followers + 1,
        },
      }));
    } catch (error) {
      console.error("Error following profile:", error);
      setActionInProgress(false);
    }
  };

  const handleUnfollow = async () => {
    try {
      setActionInProgress(true);
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/profiles/${name}/unfollow`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to unfollow profile");
      }
      setIsFollowing(false);
      setActionInProgress(false);
      localStorage.removeItem(`${name}-follow`);
      setProfile((prevProfile) => ({
        ...prevProfile,
        _count: {
          ...prevProfile._count,
          followers: prevProfile._count.followers - 1,
        },
      }));
    } catch (error) {
      console.error("Error unfollowing profile:", error);
      setActionInProgress(false);
    }
  };
  const handleCommentSubmit = async (postId) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}/comments`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${authToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            text: commentText,
          }),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to add comment");
      }
      fetchCommentsForPost(postId);
      setCommentText("");
    } catch (error) {
      console.error("Error adding comment:", error);
    }
  };

  const fetchCommentsForPost = async (postId) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}/comments`,
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch comments for post");
      }
      const commentsData = await response.json();
      setComments((prevComments) => ({
        ...prevComments,
        [postId]: commentsData,
      }));
    } catch (error) {
      console.error("Error fetching comments for post:", error);
    }
  };

  useEffect(() => {
    posts.forEach((post) => {
      fetchCommentsForPost(post.id);
    });
  }, [posts]);

  return (
    <div>
      <Navbar />

      <div className="container">
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>Error: {error}</p>
        ) : !profile ? (
          <p>Profile not found</p>
        ) : (
          <div className="row justify-content-center">
            <div className="col-md-8">
              <h1 className="text-center">Profile Page for {name}</h1>
              <div className="card" id="profile-card">
                <div className="card-body" id="profile-body">
                  <div className="text-center">
                    <p>
                      {" "}
                      <img
                        src={profile.avatar}
                        alt="Avatar"
                        style={{ maxWidth: "200px" }}
                      />
                    </p>
                    <p>
                      {" "}
                      <img
                        src={profile.banner}
                        alt="Banner"
                        style={{ maxWidth: "200px" }}
                      />
                    </p>
                    <p>Name: {profile.name}</p>
                    <p>Followers: {profile._count.followers}</p>
                    <p>Following: {profile._count.following}</p>
                    <p>Posts: {profile._count.posts}</p>
                    {actionInProgress ? (
                      <p>Updating follow status...</p>
                    ) : (
                      <button
                        onClick={isFollowing ? handleUnfollow : handleFollow}
                        className="btn btn-primary"
                      >
                        {isFollowing ? "Unfollow" : "Follow"}
                      </button>
                    )}
                  </div>
                </div>
              </div>

              <div>
                <h2>Posts by {name}</h2>
                <div className="row">
                  {posts.map((post) => (
                    <div className="col-md-4 mb-4" key={post.id}>
                      <div className="card">
                        <div className="card-body">
                          <h5 className="card-title">{post.title}</h5>
                          <p className="card-text">{post.body}</p>
                        </div>
                        <div className="card-footer">
                          <h6>Comments</h6>
                          <ul className="list-unstyled">
                            {comments[post.id] &&
                              comments[post.id].map((comment) => (
                                <li key={comment.id}>{comment.text}</li>
                              ))}
                          </ul>
                          <form
                            onSubmit={(e) => {
                              e.preventDefault();
                              handleCommentSubmit(post.id);
                            }}
                          >
                            <div className="mb-3">
                              <textarea
                                className="form-control"
                                placeholder="Add a comment..."
                                value={commentText}
                                onChange={(e) => setCommentText(e.target.value)}
                              />
                            </div>
                            <button type="submit" className="btn btn-primary">
                              Comment
                            </button>
                          </form>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profiles;
