import React, { useState, useEffect } from "react";
import Navbar from "../Components/Layouts/Navbar";
import { Link } from "react-router-dom";
import CommentForm from "./Post/CommentForm";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const storedPosts = JSON.parse(localStorage.getItem("posts"));
    if (storedPosts) {
      setPosts(storedPosts);
    } else {
      fetchPosts();
    }
  }, []);

  const fetchPosts = async () => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        "https://nf-api.onrender.com/api/v1/social/posts",
        {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch posts");
      }
      const postData = await response.json();
      setPosts(postData);
      localStorage.setItem("posts", JSON.stringify(postData));
    } catch (error) {
      console.error("Error fetching posts:", error);
      setError(error.message);
    }
  };

  const handleCommentSubmit = async (postId, comment) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}/comment`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
          body: JSON.stringify({ body: comment }),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to submit comment");
      }
      const updatedPosts = posts.map((post) =>
        post.id === postId
          ? {
              ...post,
              comments: [...(post.comments || []), { body: comment }],
            }
          : post
      );
      setPosts(updatedPosts);
      localStorage.setItem("posts", JSON.stringify(updatedPosts));
    } catch (error) {
      console.error("Error submitting comment:", error);
    }
  };

  const handleReactToPost = async (postId) => {
    try {
      const authToken = localStorage.getItem("accessToken");
      const response = await fetch(
        `https://nf-api.onrender.com/api/v1/social/posts/${postId}/react`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to react to post");
      }
      const updatedPosts = posts.map((post) =>
        post.id === postId
          ? {
              ...post,
              reacted: !post.reacted,
            }
          : post
      );
      setPosts(updatedPosts);
      localStorage.setItem("posts", JSON.stringify(updatedPosts));
    } catch (error) {
      console.error("Error reacting to post:", error);
    }
  };

  return (
    <div>
      <Navbar />
      <h2>List of Posts</h2>
      {error && <p>Error: {error}</p>}
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <Link to={`/posts/${post.id}`} key={post.id}>
              {post.title}
            </Link>
            <CommentForm
              postId={post.id}
              onCommentSubmit={handleCommentSubmit}
            />
            <button
              onClick={() => handleReactToPost(post.id)}
              className="btn btn-link"
            >
              {post.reacted ? (
                <i className="bi bi-heart-fill"></i>
              ) : (
                <i className="bi bi-heart"></i>
              )}
            </button>
            {post.comments && (
              <ul>
                {post.comments.map((comment, index) => (
                  <li key={index}>{comment.body}</li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Posts;
